        public void Run()
        {
            var floor = 0;
            var chars = INPUT.ToCharArray();

            Console.WriteLine($"Beginning at floor {floor}...");

            for (var pos = 0; pos < chars.Length; pos++)
            {
                switch (chars[pos])
                {
                    case '(':
                        floor += 1;
                        Console.WriteLine($"\tGoing up to floor {floor}...");
                        break;

                    case ')':
                        floor -= 1;
                        if (floor < 0){
                            Console.WriteLine($"Going to the basement for the first time at position {pos + 1}");
                            return;
                        }
                        Console.WriteLine($"\tGoing down to floor {floor}...");
                        break;

                    default:
                        Console.WriteLine($"\n\n\tUnexpected char at position {pos}: {chars[pos]}!\n\n");
                        break;
                }
            }

            Console.WriteLine($"Done! Arrived at floor {floor}.");
        }
